import {react, useState} from 'react';
import VideoPop from 'react-video-pop';
import './Demo.css';





const Demo= ({url})=>
{

    
var modal = document.getElementById("myModal");
var vid = document.getElementById("myImg");
var modalvid = document.getElementById("img01");

vid.onclick = function(){
  modal.style.display = "block";
  modalvid.src = {url};
}



var span = document.getElementsByClassName("close")[0];


span.onclick = function() { 
  modal.style.display = "none";
}

  





return (
<>

<video id='myimg' width="320" height="240" autoplay>
        <source src={url}  type="video/mp4"/>
</video>




<div id="myModal" className="modal">
  <span className="close">&times;</span>
    <video width="320" height="240" autoplay>
        <source src={url} className="modal-content" id="img01" type="video/mp4"/>
    </video>
</div>

   
  

 


<div className="demo__video">

</div>
    

</>


)



}


export default Demo;
